import { Canvas, useLoader} from '@react-three/fiber';
import { TextureLoader } from 'three/src/loaders/TextureLoader';
import { Stars} from '@react-three/drei';
import Ocean from './Ocean';
import TextureSphere2 from './texture/TextureSphere2.jpg'
export default function SceneBas(){
    const colorMap =useLoader(TextureLoader, TextureSphere2);
    return (
    <div className='sceneBas'>
        <Canvas>


        <Stars className='stars' radius={40} depth={70} count={9000} factor={4} saturation={50} fade speed={2} />
        <ambientLight intensity={0.5} />
        <directionalLight position ={[-7,1,-5]} intensity={0.9}/>
        <Ocean/>
        <mesh rotation={[25,6,20]}>
                <sphereBufferGeometry attach ="geometry" args={[0.7, 30, 30]}/>
                {/*<meshStandardMaterial attach ="material" transparent opacity={0.5} color='#816EFC' metalness={0.5}/> */}
                <meshStandardMaterial map={colorMap}/>

            </mesh>
        <pointLight position={[6, 3, 5]} />

    </Canvas>
    </div>
    )
}
